mapper.select:
select *
from troubleshooting_record


        section = #{section}


        status = #{status}


        name like concat('%', #{name}, '%')


        and spec like concat('%', #{spec}, '%')


        and repairman like concat('%', #{repairman}, '%')


        and completeTime between #{completeTimeStart} and #{completeTimeEnd}


Order by completeTime desc
